package hackingjava.thinkinginjava.chapter21;public class Join {	public static void main(String[] args) {		Sleeper			sleeper1 = new Sleeper("sleeper1", 2000),			sleeper2 = new Sleeper("sleeper2", 2000);		Joiner			joiner1 = new Joiner("joiner1", sleeper1),			joiner2 = new Joiner("joiner2", sleeper2);				sleeper1.interrupt();	}}class Sleeper extends Thread {	private int duration;	public Sleeper(String name, int duration) {		super(name);		this.duration = duration;		start();	}	public void run() {		try {			sleep(duration);		} catch (InterruptedException e) {			System.out.println(getName() + " interrupted.");			return;		}		System.out.println(getName() + " awakened");	}}class Joiner extends Thread {	private Sleeper sleeper;	public Joiner(String name, Sleeper sleeper) {		super(name);		this.sleeper = sleeper;		start();	}	public void run() {		try {			sleeper.join();		} catch (InterruptedException e) {			System.out.println(getName() + " interrupted");		}		System.out.println(getName() + " join completed");	}}